import {useEffect, useReducer, useState} from 'react';
import {useParams} from 'react-router';
import {ErrorPage, LoadingPage} from 'pages';
import {SECRET_KEY, SERVER} from 'utils/env';
import {Text, TextInput, View, Separator, Button} from 'components';
import styles from './.module.css';
import {resetPassword as resetPasswordReducer, resetPasswordInitState} from 'hooks';

export default function HomePage() {
  const {token} = useParams();
  const [request, setRequest] = useState('pending');
  const [state, setState] = useReducer(resetPasswordReducer, resetPasswordInitState);
  const [encrpyt, setEncrypt] = useState({input1: true, input2: true});

  useEffect(() => {
    fetch(`${SERVER}/${SECRET_KEY}/api/service/reset-password-auth`, {
      headers: {'reset-auth-token': token},
    })
      .then(res => res.json())
      .then(res => {
        if (!res.status) {
          setRequest('failed');
          return;
        }
        setRequest('success');
      });
  }, []);

  const onClickChange = () => {
    fetch(``);
  };

  if (request === 'success') {
    return (
      <View style={styles.mainPane}>
        <View style={styles.topPane}>
          <Text style={styles.title}>New Password</Text>
          <Separator vertical={0.25} />
          <Text style={styles.subtitle}>
            Please be sure, the password you enter was not leaked to anyone
          </Text>
        </View>
        <Separator vertical={2} />
        <View style={styles.bodyPane}>
          <TextInput
            skinStyle={styles.inputSkin}
            placeholder='New Password'
            isTextEncrypt={state.encrypt.input1}
            onTextEncrypt={() =>
              setState({type: 'set', encrpyt: {input1: !state.encrypt.input1}})
            }
          />
          <Separator vertical={0.5} />
          <TextInput
            skinStyle={styles.inputSkin}
            placeholder='Confirm Password'
            isTextEncrypt={state.encrypt.input2}
            onTextEncrypt={() =>
              setState({type: 'set', encrpyt: {input2: !state.encrypt.input2}})
            }
          />
          <Separator vertical={1} />
          <Button style={styles.button} title='Change' onClick={onClickChange} />
        </View>
      </View>
    );
  }

  if (request === 'failed') {
    return <ErrorPage />;
  }

  return <LoadingPage />;
}
