import {Icon, View} from 'components';
import {ACCENT_COLOR} from 'constants/colors';
import {forwardRef} from 'react';
import styles from './.module.css';

export default forwardRef(
  (
    {
      id,
      skinStyle,
      bodyStyle,
      value,
      onChangeText,
      prefixComponent,
      suffixComponent,
      placeholder,
      isTextEncrypt,
      ...props
    },
    ref,
  ) => {
    const onTextEncrypt = status => {
      if (status) {
        return <Icon font='Feather' name='eye-off' size='3vh' color={ACCENT_COLOR} />;
      }
      return <Icon font='Feather' name='eye' size='3vh' color={ACCENT_COLOR} />;
    };

    return (
      <View style={`${styles.inputPane} ${skinStyle}`}>
        {prefixComponent && (
          <View style={styles.prefixComponentPane}>{prefixComponent}</View>
        )}
        <input
          id={id}
          ref={ref}
          className={`${styles.input} ${bodyStyle}`}
          value={value}
          onChange={evt => (onChangeText ? onChangeText(evt.target.value) : evt)}
          placeholder={placeholder}
          {...props}
        />
        {placeholder.toLowerCase().includes('password') && onTextEncrypt(isTextEncrypt)}
      </View>
    );
  },
);
