import {isObject, isTypeof} from 'utils/checker';

export const resetPasswordInitState = {
  encryptInput1: true,
  encryptInput2: true,
  newPassword: '',
  confirmPassword: '',
  request: 'pending',
};

export default function resetPassword(state = resetPasswordInitState, action) {
  switch (action.type) {
    case 'set':
      return {
        encryptInput1: isTypeof('boolean', action.input1, state.encryptInput1),
        encryptInput2: isTypeof('boolean', action.input2, state.encryptInput2),
        newPassword: isTypeof('string', action.newPassword, state.newPassword),
        confirmPassword: isTypeof(
          'string',
          action.confirmPassword,
          state.confirmPassword,
        ),
        request: isTypeof('string', action.request, state.request),
      };

    case 'clear':
      return resetPasswordInitState;

    default:
      return state;
  }
}
