import {useEffect, useReducer, useState} from 'react';
import {useParams} from 'react-router';
import {ErrorPage, LoadingPage} from 'pages';
import {SECRET_KEY, SERVER} from 'utils/env';
import {Text, TextInput, View, Separator, Button} from 'components';
import styles from './.module.css';
import {resetPassword as resetPasswordReducer, resetPasswordInitState} from 'hooks';
import {
  hasLetter,
  hasLowerCaseLetter,
  hasNumber,
  hasSymbol,
  hasUpperCaseLetter,
} from 'utils/checker';

export default function HomePage() {
  const {token} = useParams();
  const [state, setState] = useReducer(resetPasswordReducer, resetPasswordInitState);

  useEffect(() => {
    fetch(`${SERVER}/${SECRET_KEY}/api/service/reset-password-auth`, {
      headers: {'reset-auth-token': token},
    })
      .then(res => res.json())
      .then(res => {
        if (!res.status) {
          setState({type: 'set', request: 'failed'});
          return;
        }
        console.log(res);
        setState({type: 'set', request: 'success'});
      });
  }, []);

  const strength = val => {
    let strength = 0;
    if (hasUpperCaseLetter(val)) {
      if (val.length > 2) {
        strength += 15;
      }
      strength += 10;
    }
    if (hasLowerCaseLetter(val)) {
      if (val.length > 4) {
        strength += 15;
      }
      strength += 10;
    }
    if (hasSymbol(val)) {
      if (val.length > 6) {
        strength += 15;
      }
      strength += 10;
    }
    if (hasNumber(val)) {
      if (val.length > 8) {
        strength += 15;
      }
      strength += 10;
    }
    return strength;
  };
  const onChangeText = (component, val) => {
    if (component === 'new-password') {
      setState({
        type: 'set',
        newPassword: val,
        passwordStrength: strength(val),
        isMatched: val === state.confirmPassword,
      });
      return;
    }
    if (component === 'confirm-password') {
      setState({
        type: 'set',
        confirmPassword: val,
        isMatched: val === state.newPassword,
      });
      return;
    }
  };

  const onClickChange = () => {
    console.log(state);
  };

  if (state.request === 'success') {
    return (
      <View style={styles.mainPane}>
        <View style={styles.topPane}>
          <Text style={styles.title}>New Password</Text>
          <Separator vertical={0.25} />
          <Text style={styles.subtitle}>
            Please be sure, the password you enter was not leaked to anyone
          </Text>
        </View>
        <Separator vertical={2} />
        <View style={styles.bodyPane}>
          <TextInput
            skinStyle={styles.inputSkin}
            placeholder='New Password'
            showIndicator={true}
            indicatorProgress={state.passwordStrength}
            isTextEncrypt={state.isEncrypt}
            value={state.newPassword}
            onChangeText={text => onChangeText('new-password', text)}
          />
          <Separator vertical={0.5} />
          <TextInput
            skinStyle={styles.inputSkin}
            placeholder='Confirm Password'
            isTextEncrypt={state.isEncrypt}
            onTextEncrypt={() => setState({type: 'set', isEncrypt: !state.isEncrypt})}
            value={state.confirmPassword}
            onChangeText={text => onChangeText('confirm-password', text)}
          />
          <Separator vertical={1} />
          <Button style={styles.button} title='Change' onClick={onClickChange} />
        </View>
      </View>
    );
  }

  if (state.request === 'failed') {
    return <ErrorPage />;
  }

  return <LoadingPage />;
}
