import {Text, View} from 'components';
import {ErrorPage, LoadingPage} from 'pages';
import {useEffect, useState} from 'react';
import {useParams} from 'react-router';
import {SECRET_KEY, SERVER} from 'utils/env';
import styles from './.module.css';

export default function HomePage() {
  const {token} = useParams();
  const [request, setRequest] = useState({status: 'pending', message: ''});

  useEffect(() => {
    fetch(`${SERVER}/${SECRET_KEY}/api/service/`, {headers: {'reset-auth-token': token}})
      .then(json => {
        console.log(`RESOLVE`, json);
      })
      .catch(err => {
        console.log(`REJECT`, err);
      });
  }, []);

  if (request.status === 'success') {
    return (
      <View style={styles.mainPane}>
        <Text style={styles.title}>Reset Password</Text>
        <Text style={styles.subtitle}>{token}</Text>
      </View>
    );
  }

  if (request.status === 'failed') {
    return <ErrorPage />;
  }

  return <LoadingPage />;
}
