import {useEffect, useState} from 'react';
import {useParams} from 'react-router';
import {ErrorPage, LoadingPage} from 'pages';
import {SECRET_KEY, SERVER} from 'utils/env';
import {Text, TextInput, View, Separator} from 'components';
import styles from './.module.css';

export default function HomePage() {
  const {token} = useParams();
  const [request, setRequest] = useState('pending');

  useEffect(() => {
    fetch(`${SERVER}/${SECRET_KEY}/api/service/reset-password-auth`, {
      headers: {'reset-auth-token': token},
    })
      .then(res => res.json())
      .then(res => {
        if (!res.status) {
          setRequest('failed');
          return;
        }
        setRequest('success');
      });
  }, []);

  if (request === 'success') {
    return (
      <View style={styles.mainPane}>
        <View style={styles.topPane}>
          <Text style={styles.title}>Reset Password</Text>
          <Text style={styles.subtitle}>
            Enter new password, make sure it follows the required format
          </Text>
        </View>
        <View style={styles.bodyPane}>
          <TextInput skinStyle={styles.inputSkin} placeholder='New Password' />
          <Separator vertical={1} />
          <TextInput skinStyle={styles.inputSkin} placeholder='Confirm Password' />
        </View>
      </View>
    );
  }

  if (request === 'failed') {
    return <ErrorPage />;
  }

  return <LoadingPage />;
}
